import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:3001';

function App() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    age: '',
    favorite_color: '',
    feedback: ''
  });
  const [submitted, setSubmitted] = useState(false);
  const [response, setResponse] = useState(null);
  const [error, setError] = useState('');
  const [submittedData, setSubmittedData] = useState(null);
  const [viewingResponses, setViewingResponses] = useState(false);
  const [allResponses, setAllResponses] = useState([]);
  const [loadingResponses, setLoadingResponses] = useState(false);

  const fetchAllResponses = async () => {
    setLoadingResponses(true);
    try {
      const response = await axios.get(`${API_URL}/responses`);
      setAllResponses(response.data);
      setLoadingResponses(false);
    } catch (err) {
      console.error('Failed to fetch responses:', err);
      setLoadingResponses(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const validateForm = () => {
    if (!formData.name.trim()) return 'Name is required';
    if (!formData.email.trim()) return 'Email is required';
    if (!formData.email.includes('@')) return 'Invalid email format';
    if (!formData.age.trim()) return 'Age is required';
    if (isNaN(Number(formData.age))) return 'Age must be a number';
    if (!formData.favorite_color.trim()) return 'Favorite color is required';
    return '';
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationError = validateForm();
    
    if (validationError) {
      setError(validationError);
      return;
    }

    const dataToSubmit = {
      ...formData,
      age: parseInt(formData.age)
    };

    try {
      const response = await axios.post(`${API_URL}/responses`, dataToSubmit);
      setResponse(response.data);
      setSubmittedData(dataToSubmit);
      setSubmitted(true);
      setError('');
      // Refresh the list of responses
      fetchAllResponses();
    } catch (err) {
      setError('Failed to submit survey. Please try again.');
    }
  };

  const resetForm = () => {
    setSubmitted(false);
    setFormData({
      name: '',
      email: '',
      age: '',
      favorite_color: '',
      feedback: ''
    });
  };

  if (viewingResponses) {
    return (
      <div className="min-h-screen bg-gray-100 py-6 flex flex-col justify-center sm:py-12">
        <div className="relative py-3 sm:max-w-xl sm:mx-auto">
          <div className="relative px-4 py-10 bg-white mx-8 md:mx-0 shadow rounded-3xl sm:p-10">
            <div className="max-w-md mx-auto">
              <div className="flex justify-between items-center mb-6">
                <h2 className="text-2xl font-bold">All Survey Responses</h2>
                <button 
                  onClick={() => setViewingResponses(false)}
                  className="px-4 py-2 bg-gray-200 rounded-md hover:bg-gray-300"
                >
                  Back to Form
                </button>
              </div>
              
              {loadingResponses ? (
                <div className="text-center py-4">Loading responses...</div>
              ) : (
                <div className="space-y-6">
                  {allResponses.length === 0 ? (
                    <div className="text-center py-4">No responses found.</div>
                  ) : (
                    allResponses.map((response, index) => (
                      <div key={response.id || index} className="bg-gray-50 p-4 rounded-lg">
                        <div className="grid grid-cols-1 gap-2">
                          <div>
                            <span className="font-medium">Name:</span> {response.name}
                          </div>
                          <div>
                            <span className="font-medium">Email:</span> {response.email}
                          </div>
                          <div>
                            <span className="font-medium">Age:</span> {response.age}
                          </div>
                          <div>
                            <span className="font-medium">Favorite Color:</span> {response.favorite_color}
                          </div>
                          {response.feedback && (
                            <div>
                              <span className="font-medium">Feedback:</span> {response.feedback}
                            </div>
                          )}
                          {response.created_at && (
                            <div className="text-sm text-gray-500">
                              Submitted: {new Date(response.created_at).toLocaleString()}
                            </div>
                          )}
                        </div>
                      </div>
                    ))
                  )}
                </div>
              )}
              
              <button 
                onClick={() => setViewingResponses(false)}
                className="mt-6 w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
              >
                Back to Survey
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (submitted && submittedData) {
    return (
      <div className="min-h-screen bg-gray-100 py-6 flex flex-col justify-center sm:py-12">
        <div className="relative py-3 sm:max-w-xl sm:mx-auto">
          <div className="relative px-4 py-10 bg-white mx-8 md:mx-0 shadow rounded-3xl sm:p-10">
            <div className="max-w-md mx-auto">
              <div className="divide-y divide-gray-200">
                <div className="py-8 text-base leading-6 space-y-4 text-gray-700 sm:text-lg sm:leading-7">
                  <h2 className="text-2xl font-bold mb-4">Thank you for your submission!</h2>
                  <p className="mb-4">Your response has been recorded successfully.</p>
                  
                  <div className="mt-6">
                    <h3 className="text-xl font-semibold mb-3">Your Submission:</h3>
                    <div className="bg-gray-50 p-4 rounded-lg">
                      <div className="grid grid-cols-1 gap-3">
                        <div>
                          <span className="font-medium">Name:</span> {submittedData.name}
                        </div>
                        <div>
                          <span className="font-medium">Email:</span> {submittedData.email}
                        </div>
                        <div>
                          <span className="font-medium">Age:</span> {submittedData.age}
                        </div>
                        <div>
                          <span className="font-medium">Favorite Color:</span> {submittedData.favorite_color}
                        </div>
                        {submittedData.feedback && (
                          <div>
                            <span className="font-medium">Feedback:</span> {submittedData.feedback}
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                  
                  <div className="flex space-x-4 mt-6">
                    <button 
                      onClick={resetForm}
                      className="flex-1 py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                    >
                      Submit Another Response
                    </button>
                    <button 
                      onClick={() => {
                        fetchAllResponses();
                        setViewingResponses(true);
                      }}
                      className="flex-1 py-2 px-4 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                    >
                      View All Responses
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 py-6 flex flex-col justify-center sm:py-12">
      <div className="relative py-3 sm:max-w-xl sm:mx-auto">
        <div className="relative px-4 py-10 bg-white mx-8 md:mx-0 shadow rounded-3xl sm:p-10">
          <div className="max-w-md mx-auto">
            <div className="divide-y divide-gray-200">
              <div className="py-8 text-base leading-6 space-y-4 text-gray-700 sm:text-lg sm:leading-7">
                <div className="flex justify-between items-center">
                  <h2 className="text-2xl font-bold">Survey Form</h2>
                  <button 
                    onClick={() => {
                      fetchAllResponses();
                      setViewingResponses(true);
                    }}
                    className="text-sm text-indigo-600 hover:text-indigo-800"
                  >
                    View Previous Responses
                  </button>
                </div>
                {error && (
                  <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4">
                    {error}
                  </div>
                )}
                <form onSubmit={handleSubmit} className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700">Name</label>
                    <input
                      type="text"
                      name="name"
                      value={formData.name}
                      onChange={handleChange}
                      className="mt-1 block w-full rounded-md border border-gray-300 px-3 py-2 shadow-sm focus:border-indigo-500 focus:outline-none focus:ring-1 focus:ring-indigo-500"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700">Email</label>
                    <input
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleChange}
                      className="mt-1 block w-full rounded-md border border-gray-300 px-3 py-2 shadow-sm focus:border-indigo-500 focus:outline-none focus:ring-1 focus:ring-indigo-500"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700">Age</label>
                    <input
                      type="number"
                      name="age"
                      value={formData.age}
                      onChange={handleChange}
                      className="mt-1 block w-full rounded-md border border-gray-300 px-3 py-2 shadow-sm focus:border-indigo-500 focus:outline-none focus:ring-1 focus:ring-indigo-500"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700">Favorite Color</label>
                    <input
                      type="text"
                      name="favorite_color"
                      value={formData.favorite_color}
                      onChange={handleChange}
                      className="mt-1 block w-full rounded-md border border-gray-300 px-3 py-2 shadow-sm focus:border-indigo-500 focus:outline-none focus:ring-1 focus:ring-indigo-500"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700">Additional Feedback</label>
                    <textarea
                      name="feedback"
                      value={formData.feedback}
                      onChange={handleChange}
                      rows={3}
                      className="mt-1 block w-full rounded-md border border-gray-300 px-3 py-2 shadow-sm focus:border-indigo-500 focus:outline-none focus:ring-1 focus:ring-indigo-500"
                    />
                  </div>
                  <button
                    type="submit"
                    className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                  >
                    Submit
                  </button>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
